import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');
  const [filter, setFilter] = useState('all'); // all, completed, incomplete
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const res = await fetch('http://localhost:5000/todos');
      const data = await res.json();
      setTodos(data);
    } catch {
      alert('Could not fetch todos!');
    }
    setLoading(false);
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!input.trim()) {
      alert('Todo cannot be empty');
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/todos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input }),
      });
      if (res.ok) {
        setInput('');
        fetchTodos();
      } else {
        alert('Could not add todo');
      }
    } catch {
      alert('Error adding todo');
    }
  };

  const startEdit = (id, text) => {
    setEditId(id);
    setEditText(text);
  };

  const handleEdit = (e) => setEditText(e.target.value);

  const submitEdit = async (id) => {
    if (!editText.trim()) {
      alert('Todo cannot be empty');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: editText }),
      });
      if (res.ok) {
        setEditId(null);
        setEditText('');
        fetchTodos();
      } else {
        alert('Could not update todo');
      }
    } catch {
      alert('Error updating todo');
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
  };

  // Toggle and delete remain as previously implemented (not shown for brevity)

  // add toggleCompleted and deleteTodo handlers from earlier steps

  const toggleCompleted = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'PATCH',
      });
      if (res.ok) fetchTodos();
      else alert('Could not toggle todo');
    } catch {
      alert('Error toggling todo');
    }
  };

  const deleteTodo = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'DELETE',
      });
      if (res.ok) fetchTodos();
      else alert('Could not delete todo');
    } catch {
      alert('Error deleting todo');
    }
  };

  return (
    <div className="container">
      <h1>MERN Todo App</h1>

      <form onSubmit={addTodo}>
        <input
          type="text"
          value={input}
          placeholder="Enter a todo..."
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Add</button>
      </form>

      {loading && <p>Loading...</p>}

      <div style={{ margin: "1rem 0" }}>
  <button onClick={() => setFilter('all')} disabled={filter === 'all'}>All</button>
  <button onClick={() => setFilter('completed')} disabled={filter === 'completed'}>Completed</button>
  <button onClick={() => setFilter('incomplete')} disabled={filter === 'incomplete'}>Incomplete</button>
</div>


      <ul>
        {todos.map((todo) => (
          <li key={todo._id} className={todo.completed ? 'completed' : ''}>
            {editId === todo._id ? (
              <>
                <input
                  type="text"
                  value={editText}
                  onChange={handleEdit}
                  onKeyDown={e => {
                    if (e.key === "Enter") submitEdit(todo._id);
                    if (e.key === "Escape") cancelEdit();
                  }}
                />
                <button onClick={() => submitEdit(todo._id)} style={ {marginLeft: "0.5rem", marginRight: "0.5rem"} }>Save</button>
                <button onClick={cancelEdit}>Cancel</button>
              </>
            ) : (
              <>
                <span onClick={() => toggleCompleted(todo._id)}>
                  {todo.text}
                </span>
                <button onClick={() => startEdit(todo._id, todo.text)} style={{ marginLeft: "0.5rem", marginRight: "0.5rem" }}>
                  Edit
                </button>
                <button onClick={() => deleteTodo(todo._id)}>
                  Delete
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
