import React, { useEffect, useState } from 'react';
import ''

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const res = await fetch('http://localhost:5000/todos');
      const data = await res.json();
      setTodos(data);
    } catch {
      alert('Could not fetch todos!');
    }
    setLoading(false);
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    try {
      const res = await fetch('http://localhost:5000/todos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input }),
      });
      if (res.ok) {
        setInput('');
        fetchTodos();
      } else {
        alert('Could not add todo');
      }
    } catch {
      alert('Error adding todo');
    }
  };

  // Toggle completed status
  const toggleCompleted = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'PATCH',
      });
      if (res.ok) fetchTodos();
      else alert('Could not toggle todo');
    } catch {
      alert('Error toggling todo');
    }
  };

  // Delete a todo
  const deleteTodo = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'DELETE',
      });
      if (res.ok) fetchTodos();
      else alert('Could not delete todo');
    } catch {
      alert('Error deleting todo');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto', padding: '2rem' }}>
      <h1>MERN Todo App</h1>

      <form onSubmit={addTodo}>
        <input
          type="text"
          value={input}
          placeholder="Enter a todo..."
          onChange={(e) => setInput(e.target.value)}
          style={{ width: '70%' }}
        />
        <button type="submit">Add</button>
      </form>

      {loading && <p>Loading...</p>}

      <ul style={{ paddingLeft: 0 }}>
        {todos.map((todo) => (
          <li
            key={todo._id}
            style={{
              textDecoration: todo.completed ? 'line-through' : 'none',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              listStyleType: 'none',
              padding: '0.5rem 0',
            }}
          >
            <span onClick={() => toggleCompleted(todo._id)} style={{ cursor: 'pointer', flex: 1 }}>
              {todo.text}
            </span>
            <button onClick={() => deleteTodo(todo._id)} style={{ marginLeft: '1rem' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
