import React, { useEffect, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');
  const [filter, setFilter] = useState('all'); // all, completed, incomplete
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const res = await fetch('http://localhost:5000/todos');
      const data = await res.json();
      setTodos(data);
      console.log(data);
    } catch {
      toast.error('Could not fetch todos. Please try again.');
    }
    setLoading(false);
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!input.trim()) {
      toast.warning('Todo cannot be empty.');
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/todos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input }),
      });
      if (res.ok) {
        setInput('');
        fetchTodos();
        toast.info('Todo added successfully.');
      } else {
        toast.error('Could not add todo. Please try again.');
      }
    } catch {
      toast.error('Error adding todo. Please check your connection.');
    }
  };

  const startEdit = (id, text) => {
    setEditId(id);
    setEditText(text);
  };

  const handleEdit = (e) => setEditText(e.target.value);

  const submitEdit = async (id) => {
    if (!editText.trim()) {
      toast.warning('Todo cannot be empty.');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: editText }),
      });
      if (res.ok) {
        setEditId(null);
        setEditText('');
        fetchTodos();
        toast.info('Todo updated successfully.');
      } else {
        toast.error('Could not update todo. Please try again.');
      }
    } catch {
      toast.error('Error updating todo. Please check your connection.');
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
  };

  const toggleCompleted = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'PATCH',
      });
      if (res.ok) {
        fetchTodos();
        toast.info('Todo marked as completed.');
      } else {
        toast.error('Could not toggle todo status. Please try again.');
      }
    } catch {
      toast.error('Error toggling todo. Please check your connection.');
    }
  };

  const deleteTodo = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/todos/${id}`, {
        method: 'DELETE',
      });
      if (res.ok) {
        fetchTodos();
        toast.info('Todo deleted successfully.');
      } else {
        toast.error('Could not delete todo. Please try again.');
      }
    } catch {
      toast.error('Error deleting todo. Please check your connection.');
    }
  };

  return (
    <div className="container">
      <h1>MERN Todo App</h1>

      <form onSubmit={addTodo}>
        <input
          type="text"
          value={input}
          placeholder="Enter a todo..."
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Add</button>
      </form>

      {loading && <p>Loading...</p>}

      <div style={{ margin: "1rem 0" }}>
        <button
          onClick={() => setFilter('all')}
          disabled={filter === 'all'}
          style={{ marginLeft: "0.5rem", marginRight: "0.5rem" }}
        >
          All
        </button>
        <button
          onClick={() => setFilter('completed')}
          disabled={filter === 'completed'}
          style={{ marginLeft: "0.5rem", marginRight: "0.5rem" }}
        >
          Completed
        </button>
        <button
          onClick={() => setFilter('incomplete')}
          disabled={filter === 'incomplete'}
          style={{ marginLeft: "0.5rem", marginRight: "0.5rem" }}
        >
          Incomplete
        </button>
      </div>

      <ul>
        {todos
          .filter(
            (todo) =>
              filter === 'all' ||
              (filter === 'completed' && todo.completed) ||
              (filter === 'incomplete' && !todo.completed)
          )
          .map((todo) => (
            <li key={todo._id} className={todo.completed ? 'completed' : ''}>
              {editId === todo._id ? (
                <>
                  <input
                    type="text"
                    value={editText}
                    onChange={handleEdit}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') submitEdit(todo._id);
                      if (e.key === 'Escape') cancelEdit();
                    }}
                  />
                  <button
                    onClick={() => submitEdit(todo._id)}
                    style={{ marginLeft: '0.5rem', marginRight: '0.5rem' }}
                  >
                    Save
                  </button>
                  <button onClick={cancelEdit}>Cancel</button>
                </>
              ) : (
                <>
                  <span onClick={() => toggleCompleted(todo._id)}>
                    {todo.text}
                  </span>
                  <span
                    style={{
                      fontSize: '0.8em',
                      color: '#666',
                      marginLeft: '1rem',
                    }}
                  >
                    {todo.createdAt &&
                      'Created: ' +
                        new Date(todo.createdAt).toLocaleString()}
                  </span>
                  <button
                    onClick={() => startEdit(todo._id, todo.text)}
                    style={{ marginLeft: '0.5rem', marginRight: '0.5rem' }}
                  >
                    Edit
                  </button>
                  <button onClick={() => deleteTodo(todo._id)}>Delete</button>
                </>
              )}
            </li>
          ))}
      </ul>

      <ToastContainer />
    </div>
  );
}

export default App;
